function initMap() {
  var myLatLng = {
    lat: -6.986886999999998,
    lng: 110.414834
  };
  var map = new google.maps.Map(document.getElementById('lokasi'), {
    zoom: 18,
    center: myLatLng
  });
  var marker = new google.maps.Marker({
    position: myLatLng,
    map: map,
    title: 'Dinas Kesehatan Kota Semarang'
  });

  var infowindow = new google.maps.InfoWindow({
    content: '<strong>Dinas Kesehatan Kota Semarang</strong><br>Jl. Pandanaran No.79, Pekunden, Kec. Semarang Tengah, Kota Semarang, Jawa Tengah 50134, Indonesia<br>'
  });

  marker.addListener('click', function() {
    infowindow.open(map, marker);
  });
}

google.maps.event.addDomListener(window, 'load', initMap);